import React, {Component} from 'react'

class EditProductForm extends Component{

    handleChange = (e) => {
        // debugger
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    editProfile = (e) => {   // create request to edit a user
        e.preventDefault()

        fetch(`http://localhost:3000/api/v1/users/${localStorage.user_id}`,{
            method: "PATCH",
            headers: {
                "Content-type": "application/json",
                // 'Accept': 'application/json',
                Authorization: `Bearer ${localStorage.token}`
            },
            body: JSON.stringify({
                username: this.state.username,
                first_name: this.state.first_name,
                last_name: this.state.last_name,
                company_name: this.state.company_name
                // title: this.state.title,
                // website_url: this.state.website_url,
                // tax_rate: this.state.tax_rate,
                // commission_rate: this.state.commission_rate

            })
        })
        .then(res => res.json())
        .then(item =>  {
            this.props.editProduct(item)
            // console.log("edit user in patch request: ", user)
        })

    }

    render(){
        // console.log("edit profile state: ", this.state)
        // console.log("edit profile props: ", this.props)


    return(
        <div>
    <form onSubmit={(e) => {
    this.handleAddNewProduct(e)
    }}>

    <p><input type='text' name="name" placeholder='Name' onChange={(e) => this.handleChange(e)}/> </p>
    <p><input type='text' name="sku" placeholder='SKU' onChange={(e) => this.handleChange(e)}/></p>
    <p><input type='text' name="color" placeholder='Color' onChange={(e) => this.handleChange(e)}/></p>
    <p><input type='text' name="price" placeholder='Price' onChange={(e) => this.handleChange(e)}/></p>
    <p><input type='text' name="onhand_qty" placeholder='Quantity On Hand' onChange={(e) => this.handleChange(e)} /></p>

    <p><input type='submit' value='Add Product'/></p>

            </form>
        </div>
        )
    }
}

export default EditProductForm
