import React from 'react'
import {Link} from 'react-router-dom'


class AddPOform extends React.Component{

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
        // console.log("inside handleChange, state: ", this.state)
    }

    handleAddNewPO = (e) => {  
        e.preventDefault()
        // console.log("Inside Handleadd method: ", this.state)
        // console.log("Inside Handleadd method, props.product: ", this.props.products)

        fetch("http://localhost:3000/api/v1/purchaseorders", {
            method: "POST",
            headers: {
                "Content-type": "application/json",
                'Accept': 'application/json',
                Authorization: `Bearer ${localStorage.token}`
            },
            body: JSON.stringify({
                date: this.state.date,
                total_paid: this.state.total_paid,
                company_name: this.state.company_name,
                description: this.state.description,
                order_num: this.state.order_num,
                // products: this.state.products

            })
        })
        .then(res => res.json())
        .then(newPO => {
            console.log("New Prodyct in addNEw method then: ", newPO.user)
            this.props.add(newPO.user)
            // this.setState({
            //     products: [...this.props.products, newProd]
            // })
        })

    }

    // handleSubmit = (e) => {
    //     e.preventDefault()
    //     console.log("state in handle submit", this.state)
    //     this.props.add(this.state)
    //   }

    goToAllPOs = () => {
        this.props.history.push("/purchaseorders")
    }

    render(){

    return(
        <div>
            {/* {console.log("Addproduct props : ", this.props)}
            {console.log("Addproduct state : ", this.state)} */}

         {localStorage.token
            ? <div>

            <form onSubmit={(e) => {
                this.handleAddNewProduct(e)
                this.goToAllPOs()
                }}>


                <p><input type='text' name="date" placeholder='Date' onChange={(e) => this.handleChange(e)}/> </p>
                <p><input type='text' name="total_paid" placeholder='Total Paid' onChange={(e) => this.handleChange(e)}/> </p>
                <p><input type='text' name="company_name" placeholder='Company Name' onChange={(e) => this.handleChange(e)}/></p>
                <p><input type='text' name="description" placeholder='Description' onChange={(e) => this.handleChange(e)}/></p>
                <p><input type='text' name="order_num" placeholder='Order Number' onChange={(e) => this.handleChange(e)}/></p>
                <p><input type='text' name="products" placeholder='Product SKU' onChange={(e) => this.handleChange(e)}/></p>
                <p><input type='submit' value='Add Purchase Order'/></p>
                {/* < button type="submit" onClick={(e) => this.handleSubmit()} >
                    Add New Product
                </button> */}

                {/* {/* <input type='submit' value='Add New Product' onClick={() => this.handleSubmit()}/> */}
            </form>
            <p><Link to="/purchaseorders">View All Purchase Orders</Link></p>

            </div>
            : <h4> Please Log In to add a new Product</h4>
        }

        </div>
    )}
}

export default AddPOform
