import React from 'react'
import add_button from './add.png'
// import delete_button from './delete.png'
// import AddToInventoryForm from './AddToInventoryForm'
import EditProductForm from './EditProductForm'

  class ProductListItem extends React.Component {

      state = {
        showEdit: false
      }


    handleChange = (e) => {
      // debugger
      this.setState({
          [e.target.name]: e.target.value
      })
  }

  handleEditProduct = () => {
    console.log("inside handleEditProduct", this.props.product.id)
    this.setState({
      showEdit: !this.state.showEdit
  })
}

        render() {

          let item = this.props.product
          return (

          <tr onClick={()=>this.handleEditProduct()}>
            <td>{item.name}</td>
            <td>{item.sku}</td>
            <td>{item.color}</td>
            <td>{item.price}</td>
            <td>{item.onhand_qty}</td>

            {/* <tr> */}
            {this.state.showEdit ? <EditProductForm/> : null } 
            </tr>
            // </tr>





        )
    }
  }

export default ProductListItem
