import React from 'react';
// import paintings from './painting'
import 'semantic-ui-css/semantic.min.css';
import NavBar from './NavBar'
import PaintingList from './ProductList'
import PaintingForm from './PaintingForm'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import Login from './Login'
import SignUp from './SignUp'



class App extends React.Component{

  constructor(){
    super()
    this.state = {
      testNew: "testnew",
      test: "test",
      form: false,
      products: []
    }
  }

  addProduct = (e) => {
    e.preventDefault()

    let newProduct = {
      name: e.target[0].value,
      color: e.target[1].value,
      price: {
        name: e.target[2].value
      }
    }

    let newArr = [...this.state.paintings, newPainting]

    this.setState({
      products: newArr
    }) 
  }

  getProducts = () => {
      fetch("http://localhost:3000/api/v1/products",
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${localStorage.token}`
        } 
      })
      .then(res => res.json())
      .then(console.log)
  }

  render(){

    return (

      <BrowserRouter>
    <div>
        <NavBar />
        <button onClick={this.getProducts}>Show Products</button>
        <Switch>

        <Route path="/login" component={Login} />

        <Route path="/signup" component={SignUp} />

        <Route path="/paintings/new" render = {(routeProps) =><PaintingForm {...routeProps} add={this.addPainting} /> } />

        <Route path="/products" render={() => <ProductList productObjs={this.state.products} />}/>

        </Switch>


    </div>
    </BrowserRouter>
  )};
}

export default App;
