import React from 'react'
import {Link} from 'react-router-dom'


class AddProductForm extends React.Component{

    state = {
        name: '',
        sku: '',
        color: '',
        price: 0,
        sale_price: 0,
        image_url: '',
        onhand_qty: 0,
        coming_in_qty: 0
      }

      setField = (e) => {
        this.setState({
          [e.target.name]: e.target.value
        })
        console.log("setField: ", e.target.name, e.target.value)
      }



    handleSubmit = (e) => {
        e.preventDefault()
        console.log("state in handle submit", this.state)
        this.props.onSubmit(this.state)
        this.setState({
          name: '',
          sku: '',
          color: '',
          price: 0,
          sale_price: 0,
          image_url: '',
          onhand_qty: 0,
          coming_in_qty: 0
        })
      }


    goHome = () => {
        this.props.history.push("/products")
    }

    render(){
    return(
        <div>
            <form className="ui form">
            {/* <form onSubmit={(e) => {
                this.props.add(e)
                this.goHome()
                }}> */}
                <input type='text' name="name" placeholder='Name' onChange={this.setField}/>
                <input type='text' name="sku" placeholder='SKU' onChange={this.setField}/>
                <input type='text' name="color" placeholder='Color' onChange={this.setField}/>
                <input type='text' name="price" placeholder='Price' onChange={this.setField}/>
                <input type='text' name="sale_price" placeholder='Sale Price' onChange={this.setField}/>
                <input type='text' name="image_url" placeholder='Image URL' onChange={this.setField}/>
                <input type='text' name="onhand_qty" placeholder='On Hand Qty' onChange={this.setField}/>
                <input type='text' name="coming_in_qty" placeholder='Coming In Qty' onChange={this.setField}/>




                <input type='submit' value='Add New Product' onClick={() => this.handleSubmit()}/>
            </form>
            <Link to="/products">
                All Products
            </Link>
        </div>
    )}
}

export default AddProductForm
