import React from 'react';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';

export default function EditProductFormDialog() {
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };


  handleChange = (e) => {
    // debugger
    this.setState({
        [e.target.name]: e.target.value
    })
}

editProduct= (e) => {   // create request to edit a product
    e.preventDefault()
    let number = this.props.product.id
    console.log("number: ", number)

    fetch(`http://localhost:3000/api/v1/products/${number}`,{
        method: "PATCH",
        headers: {
            "Content-type": "application/json",
            // 'Accept': 'application/json',
            Authorization: `Bearer ${localStorage.token}`
        },
        body: JSON.stringify({
            onhand_qty: this.state.onhand_qty,
            name: this.state.name
        })
    })
    .then(res => res.json())
    .then(item =>  {
        // this.props.updateProduct(item)
        console.log("edit product in patch request: ", item)
    })

}



  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Open form dialog
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Subscribe</DialogTitle>
        <DialogContent>
          <DialogContentText>
            To subscribe to this website, please enter your email address here. We will send updates
            occasionally.
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email Address"
            type="email"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleClose} color="primary">
            Update Product
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
