import React from 'react';
// import paintings from './painting'
import 'semantic-ui-css/semantic.min.css';
import NavBar from './NavBar'
import ProductList from './ProductList'
import {BrowserRouter, Route, Switch} from 'react-router-dom'
import Login from './Login'
import SignUp from './SignUp'
import Dashboard from './Dashboard'
// import {Link} from 'react-router-dom'
import AddProductForm from './AddProductForm'
import AddPOform from './AddPOform'
import PurchaseOrderList from './PurchaseOrderList'
import AddCustomerForm from './AddCustomerForm'
import CustomerList from './CustomerList'




class App extends React.Component{

  constructor(){
    super()
    this.state = {
      products: [],
      purchaseorders: []
    }
  }

  componentDidMount(){
    this.getProducts()
    this.getPurchaseOrders()

  }

  goToInventory = () => {
    this.state.history.push("/products")
}
goToAllPOs = () => {
  this.props.history.push("/purchaseorders")
}

  addNewProduct = (newProd) => {  
        this.setState({
            products: [...this.state.products, newProd]
        })
    // })
    // this.goToInventory()
  }

  addNewPO = (newPO) => {  
    console.log("inside App newPO: ", newPO)
    this.setState({
        purchaseorders: [...this.state.purchaseorders, newPO]
    })
    this.goToAllPOs()

}


  getProducts = () => {
      fetch("http://localhost:3000/api/v1/products",
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${localStorage.token}`
        } 
      })
      .then(res => res.json())
      .then(data => {
        this.setState({
          products: data
        })
      })   
      // console.log(this.state.products)
  }

  getPurchaseOrders = () => {
    fetch("http://localhost:3000/purchaseorders",
    {
      method: "GET",
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      } 
    })
    .then(res => res.json())
    .then(data => {
      this.setState({
        purchaseorders: data
      })
    })   
    // console.log(this.state.products)

}


  render(){

    return (
      <BrowserRouter>
    <div>
        <NavBar />

        <Switch>

              <Route path="/dashboard" component={Dashboard} />

              <Route path="/login" component={(routerProps)=><Login {...routerProps} />} />
              <Route path="/signup" component={SignUp} />

              <Route path="/products" render={() => <ProductList products={this.state.products} />}/>
              <Route path="/addproductform" component={(routerProps)=><AddProductForm {...routerProps} add={this.addNewProduct} products={this.state.products}/>} />

              <Route path="/myinventory" render={() => <ProductList products={this.state.products} />}/>
              <Route path="/addinventory" component={(routerProps)=><AddProductForm {...routerProps} add={this.addNewProduct} products={this.state.products}/>} />

              <Route path="/purchaseorders" render={() => <PurchaseOrderList purchaseorders={this.state.purchaseorders} />}/>
              <Route path="/addpoform" component={(routerProps)=><AddPOform {...routerProps} add={this.addNewPO} purchaseorders={this.state.purchaseorders}/>} />

              <Route path="/customers" render={() => <CustomerList customers={this.state.customers}/>}/>
              <Route path="/addcustomerform" component={(routerProps)=><AddCustomerForm {...routerProps} add={this.addNewCust}/>} />

        </Switch>


    </div>
    </BrowserRouter>
  )};
}

export default App;
