import React from 'react'
import add_button from './add.png'
import delete_button from './delete.png'
import AddToInventoryForm from './AddToInventoryForm'

  class ProductListItem extends React.Component {


      handleAddToInventory = () => {
        this.state.showAdd?
        this.setState({
          showAddBar: false
        })
        : this.setState({
          showAddBar: true
        })
      }

        render() {
          let item = this.props.product
          return (
            <div>
          <tr>
            <td>
            <img src={add_button} style={{width: "15px", height: "15px"}} onClick={()=>this.handleAddToInventory()}/>

            {item.name}
            </td>
            <td>{item.sku}</td>
            <td>{item.color}</td>
            <td>{item.price}</td>

          </tr>
          <div className="sidebar">
            {this.state.showAddBar ? <AddToInventoryForm onSubmit={item}/> : null } 
            </div>
          </div>
        )
      }
    }
export default ProductListItem
